
<!--
	<tabbars-v v-on:clickThis="isThis" :names = '["最新揭晓","我参与"]'></tabbars-v>
	isThis 父组件回调函数名字，传出点击按钮的index；
	name   数组，tab栏内按钮名称；
 -->
<template>
	<div class="tabbars flex">
		<div class="tabbars-item" v-on:click="clickThis(index)" v-for="(item, index) of itemName">
			<button :class="{jiahei : index == selectthis}">{{item}}</button>
			<div class="tabbars-border">
				<div class="tabbars-border-in" :class="{xuanzhong : index == selectthis}"></div>
			</div>
		</div>
	</div>
</template>
