<script type="text/javascript">
	export default {
		props: [
			"names",
			"initTab"
		],
		data() {
			return {
				itemNum: this.num,
				itemName: this.names,
				selectthis: 0
			}
		},
		components: {

		},
		methods: {
			clickThis(index) {
				this.selectthis = index;
				this.$emit('clickThis', index);
			}
		},
		created() {
			if(this.initTab != undefined) {
				this.selectthis = this.initTab;
			}
		}
	}
</script>